mapper.update:
update dca_b_auditfive
user_account=#{userAccount}, ad_content=#{adContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from dca_b_auditfive
where  1=1
     and dcaBAuditfive.id =#{ dcaBAuditfive.id} 
     and dcaBAuditfive.user_account like concat('%', #{ dcaBAuditfive.userAccount} ,'%')
     and dcaBAuditfive.ad_content like concat('%', #{ dcaBAuditfive.adContent} ,'%')
 and dcaBAuditfive.state =#{ dcaBAuditfive.state} 
 and dcaBAuditfive.IS_DELETEMARK =#{ dcaBAuditfive.isDeletemark} 

    AND dcaBAuditfive.CREATE_TIME  >=   #{dcaBAuditfive.createTimeFrom}


    AND dcaBAuditfive.CREATE_TIME  <=   #{dcaBAuditfive.createTimeTo}


    AND dcaBAuditfive.MODIFY_TIME  >=   #{dcaBAuditfive.modifyTimeFrom}


    AND dcaBAuditfive.MODIFY_TIME  <=   #{dcaBAuditfive.modifyTimeTo}
