mapper.update:
update dca_b_fivecomment
user_account=#{userAccount}, five_percent=#{fivePercent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from dca_b_fivecomment
where  1=1
     and dcaBFivecomment.id =#{ dcaBFivecomment.id} 
     and dcaBFivecomment.user_account like concat('%', #{ dcaBFivecomment.userAccount} ,'%')
     and dcaBFivecomment.five_percent like concat('%', #{ dcaBFivecomment.fivePercent} ,'%')
 and dcaBFivecomment.state =#{ dcaBFivecomment.state} 
 and dcaBFivecomment.IS_DELETEMARK =#{ dcaBFivecomment.isDeletemark} 

    AND dcaBFivecomment.CREATE_TIME  >=   #{dcaBFivecomment.createTimeFrom}


    AND dcaBFivecomment.CREATE_TIME  <=   #{dcaBFivecomment.createTimeTo}


    AND dcaBFivecomment.MODIFY_TIME  >=   #{dcaBFivecomment.modifyTimeFrom}


    AND dcaBFivecomment.MODIFY_TIME  <=   #{dcaBFivecomment.modifyTimeTo}
