mapper.update:
update dca_b_innovatebuild
user_account=#{userAccount}, project_name=#{projectName}, project_type=#{projectType}, project_source=#{projectSource}, contract_fund=#{contractFund}, real_fund=#{realFund}, audit_date=#{auditDate}, start_date=#{startDate}, end_date=#{endDate}, rank_num=#{rankNum}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from dca_b_innovatebuild
where  1=1
     and dcaBInnovatebuild.id =#{ dcaBInnovatebuild.id} 
     and dcaBInnovatebuild.project_name like concat('%', #{ dcaBInnovatebuild.projectName} ,'%')
     and dcaBInnovatebuild.project_type like concat('%', #{ dcaBInnovatebuild.projectType} ,'%')
     and dcaBInnovatebuild.project_source like concat('%', #{ dcaBInnovatebuild.projectSource} ,'%')

    AND dcaBInnovatebuild.audit_date  >=   #{dcaBInnovatebuild.auditDateFrom}


    AND dcaBInnovatebuild.audit_date  <=   #{dcaBInnovatebuild.auditDateTo}


    AND dcaBInnovatebuild.start_date  >=   #{dcaBInnovatebuild.startDateFrom}


    AND dcaBInnovatebuild.start_date  <=   #{dcaBInnovatebuild.startDateTo}


    AND dcaBInnovatebuild.end_date  >=   #{dcaBInnovatebuild.endDateFrom}


    AND dcaBInnovatebuild.end_date  <=   #{dcaBInnovatebuild.endDateTo}

 and dcaBInnovatebuild.rank_num =#{ dcaBInnovatebuild.rankNum} 
 and dcaBInnovatebuild.state =#{ dcaBInnovatebuild.state} 
 and dcaBInnovatebuild.IS_DELETEMARK =#{ dcaBInnovatebuild.isDeletemark} 

    AND dcaBInnovatebuild.CREATE_TIME  >=   #{dcaBInnovatebuild.createTimeFrom}


    AND dcaBInnovatebuild.CREATE_TIME  <=   #{dcaBInnovatebuild.createTimeTo}


    AND dcaBInnovatebuild.MODIFY_TIME  >=   #{dcaBInnovatebuild.modifyTimeFrom}


    AND dcaBInnovatebuild.MODIFY_TIME  <=   #{dcaBInnovatebuild.modifyTimeTo}
