mapper.update:
update dca_b_parttimejob
user_account=#{userAccount}, jz_start_time=#{jzStartTime}, jz_end_time=#{jzEndTime}, jz_content=#{jzContent}, jz_state=#{jzState}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from dca_b_parttimejob
where  1=1
     and dcaBParttimejob.id =#{ dcaBParttimejob.id} 
     and dcaBParttimejob.user_account like concat('%', #{ dcaBParttimejob.userAccount} ,'%')

    AND dcaBParttimejob.jz_start_time  >=   #{dcaBParttimejob.jzStartTimeFrom}


    AND dcaBParttimejob.jz_start_time  <=   #{dcaBParttimejob.jzStartTimeTo}


    AND dcaBParttimejob.jz_end_time  >=   #{dcaBParttimejob.jzEndTimeFrom}


    AND dcaBParttimejob.jz_end_time  <=   #{dcaBParttimejob.jzEndTimeTo}

     and dcaBParttimejob.jz_content like concat('%', #{ dcaBParttimejob.jzContent} ,'%')
 and dcaBParttimejob.jz_state =#{ dcaBParttimejob.jzState} 
 and dcaBParttimejob.IS_DELETEMARK =#{ dcaBParttimejob.isDeletemark} 

    AND dcaBParttimejob.CREATE_TIME  >=   #{dcaBParttimejob.createTimeFrom}


    AND dcaBParttimejob.CREATE_TIME  <=   #{dcaBParttimejob.createTimeTo}


    AND dcaBParttimejob.MODIFY_TIME  >=   #{dcaBParttimejob.modifyTimeFrom}


    AND dcaBParttimejob.MODIFY_TIME  <=   #{dcaBParttimejob.modifyTimeTo}
