mapper.update:
update dca_b_prizeorpunish
file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, pp_start_time=#{ppStartTime}, pp_end_time=#{ppEndTime}, pp_content=#{ppContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_prizeorpunish
where  1=1
     and dcaBPrizeorpunish.id =#{ dcaBPrizeorpunish.id} 
     and dcaBPrizeorpunish.file_id like concat('%', #{ dcaBPrizeorpunish.fileId} ,'%')
     and dcaBPrizeorpunish.file_url like concat('%', #{ dcaBPrizeorpunish.fileUrl} ,'%')
     and dcaBPrizeorpunish.user_account_name like concat('%', #{ dcaBPrizeorpunish.userAccountName} ,'%')
     and dcaBPrizeorpunish.user_account like concat('%', #{ dcaBPrizeorpunish.userAccount} ,'%')

    AND dcaBPrizeorpunish.pp_start_time  >=   #{dcaBPrizeorpunish.ppStartTimeFrom}


    AND dcaBPrizeorpunish.pp_start_time  <=   #{dcaBPrizeorpunish.ppStartTimeTo}


    AND dcaBPrizeorpunish.pp_end_time  >=   #{dcaBPrizeorpunish.ppEndTimeFrom}


    AND dcaBPrizeorpunish.pp_end_time  <=   #{dcaBPrizeorpunish.ppEndTimeTo}

     and dcaBPrizeorpunish.pp_content like concat('%', #{ dcaBPrizeorpunish.ppContent} ,'%')
 and dcaBPrizeorpunish.state =#{ dcaBPrizeorpunish.state} 
 and dcaBPrizeorpunish.IS_DELETEMARK =#{ dcaBPrizeorpunish.isDeletemark} 

    AND dcaBPrizeorpunish.CREATE_TIME  >=   #{dcaBPrizeorpunish.createTimeFrom}


    AND dcaBPrizeorpunish.CREATE_TIME  <=   #{dcaBPrizeorpunish.createTimeTo}


    AND dcaBPrizeorpunish.MODIFY_TIME  >=   #{dcaBPrizeorpunish.modifyTimeFrom}


    AND dcaBPrizeorpunish.MODIFY_TIME  <=   #{dcaBPrizeorpunish.modifyTimeTo}

     and dcaBPrizeorpunish.auditMan like concat('%', #{ dcaBPrizeorpunish.auditMan} ,'%')
     and dcaBPrizeorpunish.auditManName like concat('%', #{ dcaBPrizeorpunish.auditManName} ,'%')

    AND dcaBPrizeorpunish.auditDate  >=   #{dcaBPrizeorpunish.auditDateFrom}


    AND dcaBPrizeorpunish.auditDate  <=   #{dcaBPrizeorpunish.auditDateTo}

     and dcaBPrizeorpunish.auditSuggestion like concat('%', #{ dcaBPrizeorpunish.auditSuggestion} ,'%')
