mapper.update:
update dca_b_sciencepublish
user_account=#{userAccount}, paper_name=#{paperName}, journal_name=#{journalName}, journal_code=#{journalCode}, paper_publishdate=#{paperPublishdate}, paper_shoulu=#{paperShoulu}, paper_cause=#{paperCause}, is_best=#{isBest}, other_times=#{otherTimes}, author_rank=#{authorRank}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from dca_b_sciencepublish
where  1=1
     and dcaBSciencepublish.id =#{ dcaBSciencepublish.id} 
     and dcaBSciencepublish.user_account like concat('%', #{ dcaBSciencepublish.userAccount} ,'%')
     and dcaBSciencepublish.paper_name like concat('%', #{ dcaBSciencepublish.paperName} ,'%')
     and dcaBSciencepublish.journal_name like concat('%', #{ dcaBSciencepublish.journalName} ,'%')
     and dcaBSciencepublish.journal_code like concat('%', #{ dcaBSciencepublish.journalCode} ,'%')

    AND dcaBSciencepublish.paper_publishdate  >=   #{dcaBSciencepublish.paperPublishdateFrom}


    AND dcaBSciencepublish.paper_publishdate  <=   #{dcaBSciencepublish.paperPublishdateTo}

     and dcaBSciencepublish.paper_shoulu like concat('%', #{ dcaBSciencepublish.paperShoulu} ,'%')
     and dcaBSciencepublish.paper_cause like concat('%', #{ dcaBSciencepublish.paperCause} ,'%')
     and dcaBSciencepublish.is_best like concat('%', #{ dcaBSciencepublish.isBest} ,'%')
     and dcaBSciencepublish.other_times like concat('%', #{ dcaBSciencepublish.otherTimes} ,'%')
     and dcaBSciencepublish.author_rank like concat('%', #{ dcaBSciencepublish.authorRank} ,'%')
 and dcaBSciencepublish.state =#{ dcaBSciencepublish.state} 
 and dcaBSciencepublish.IS_DELETEMARK =#{ dcaBSciencepublish.isDeletemark} 

    AND dcaBSciencepublish.CREATE_TIME  >=   #{dcaBSciencepublish.createTimeFrom}


    AND dcaBSciencepublish.CREATE_TIME  <=   #{dcaBSciencepublish.createTimeTo}


    AND dcaBSciencepublish.MODIFY_TIME  >=   #{dcaBSciencepublish.modifyTimeFrom}


    AND dcaBSciencepublish.MODIFY_TIME  <=   #{dcaBSciencepublish.modifyTimeTo}
