mapper.update:
update dca_b_sciencesearch
user_account_name=#{userAccountName}, user_account=#{userAccount}, project_name=#{projectName}, project_type=#{projectType}, project_source=#{projectSource}, contract_fund=#{contractFund}, real_fund=#{realFund}, audit_date=#{auditDate2}, start_date=#{startDate}, end_date=#{endDate}, rank_num=#{rankNum}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_sciencesearch
where  1=1
     and dcaBSciencesearch.id =#{ dcaBSciencesearch.id} 
     and dcaBSciencesearch.user_account_name like concat('%', #{ dcaBSciencesearch.userAccountName} ,'%')
     and dcaBSciencesearch.user_account like concat('%', #{ dcaBSciencesearch.userAccount} ,'%')
     and dcaBSciencesearch.project_name like concat('%', #{ dcaBSciencesearch.projectName} ,'%')
     and dcaBSciencesearch.project_type like concat('%', #{ dcaBSciencesearch.projectType} ,'%')
     and dcaBSciencesearch.project_source like concat('%', #{ dcaBSciencesearch.projectSource} ,'%')


    AND dcaBSciencesearch.start_date  >=   #{dcaBSciencesearch.startDateFrom}


    AND dcaBSciencesearch.start_date  <=   #{dcaBSciencesearch.startDateTo}


    AND dcaBSciencesearch.end_date  >=   #{dcaBSciencesearch.endDateFrom}


    AND dcaBSciencesearch.end_date  <=   #{dcaBSciencesearch.endDateTo}

 and dcaBSciencesearch.rank_num =#{ dcaBSciencesearch.rankNum} 
 and dcaBSciencesearch.state =#{ dcaBSciencesearch.state} 
 and dcaBSciencesearch.IS_DELETEMARK =#{ dcaBSciencesearch.isDeletemark} 

    AND dcaBSciencesearch.CREATE_TIME  >=   #{dcaBSciencesearch.createTimeFrom}


    AND dcaBSciencesearch.CREATE_TIME  <=   #{dcaBSciencesearch.createTimeTo}


    AND dcaBSciencesearch.MODIFY_TIME  >=   #{dcaBSciencesearch.modifyTimeFrom}


    AND dcaBSciencesearch.MODIFY_TIME  <=   #{dcaBSciencesearch.modifyTimeTo}

     and dcaBSciencesearch.auditMan like concat('%', #{ dcaBSciencesearch.auditMan} ,'%')
     and dcaBSciencesearch.auditManName like concat('%', #{ dcaBSciencesearch.auditManName} ,'%')

    AND dcaBSciencesearch.auditDate  >=   #{dcaBSciencesearch.auditDateFrom}


    AND dcaBSciencesearch.auditDate  <=   #{dcaBSciencesearch.auditDateTo}

     and dcaBSciencesearch.auditSuggestion like concat('%', #{ dcaBSciencesearch.auditSuggestion} ,'%')
