mapper.update:
update dca_b_scientificprize
user_account_name=#{userAccountName}, user_account=#{userAccount}, sp_project_name=#{spProjectName}, sr_project_grade=#{srProjectGrade}, sr_project_level=#{srProjectLevel}, sr_prize_dept=#{srPrizeDept}, sr_prize_date=#{srPrizeDate}, sr_prize_ranknum=#{srPrizeRanknum}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_scientificprize
where  1=1
     and dcaBScientificprize.id =#{ dcaBScientificprize.id} 
     and dcaBScientificprize.user_account_name like concat('%', #{ dcaBScientificprize.userAccountName} ,'%')
     and dcaBScientificprize.user_account like concat('%', #{ dcaBScientificprize.userAccount} ,'%')
     and dcaBScientificprize.sp_project_name like concat('%', #{ dcaBScientificprize.spProjectName} ,'%')
     and dcaBScientificprize.sr_project_grade like concat('%', #{ dcaBScientificprize.srProjectGrade} ,'%')
     and dcaBScientificprize.sr_project_level like concat('%', #{ dcaBScientificprize.srProjectLevel} ,'%')
     and dcaBScientificprize.sr_prize_dept like concat('%', #{ dcaBScientificprize.srPrizeDept} ,'%')

    AND dcaBScientificprize.sr_prize_date  >=   #{dcaBScientificprize.srPrizeDateFrom}


    AND dcaBScientificprize.sr_prize_date  <=   #{dcaBScientificprize.srPrizeDateTo}

 and dcaBScientificprize.sr_prize_ranknum =#{ dcaBScientificprize.srPrizeRanknum} 
 and dcaBScientificprize.state =#{ dcaBScientificprize.state} 
 and dcaBScientificprize.IS_DELETEMARK =#{ dcaBScientificprize.isDeletemark} 

    AND dcaBScientificprize.CREATE_TIME  >=   #{dcaBScientificprize.createTimeFrom}


    AND dcaBScientificprize.CREATE_TIME  <=   #{dcaBScientificprize.createTimeTo}


    AND dcaBScientificprize.MODIFY_TIME  >=   #{dcaBScientificprize.modifyTimeFrom}


    AND dcaBScientificprize.MODIFY_TIME  <=   #{dcaBScientificprize.modifyTimeTo}

     and dcaBScientificprize.auditMan like concat('%', #{ dcaBScientificprize.auditMan} ,'%')
     and dcaBScientificprize.auditManName like concat('%', #{ dcaBScientificprize.auditManName} ,'%')

    AND dcaBScientificprize.auditDate  >=   #{dcaBScientificprize.auditDateFrom}


    AND dcaBScientificprize.auditDate  <=   #{dcaBScientificprize.auditDateTo}

     and dcaBScientificprize.auditSuggestion like concat('%', #{ dcaBScientificprize.auditSuggestion} ,'%')
