mapper.update:
update dca_b_teacherqualify
user_account=#{userAccount}, tq_code=#{tqCode}, tq_receive_date=#{tqReceiveDate}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from dca_b_teacherqualify
where  1=1
     and dcaBTeacherqualify.id =#{ dcaBTeacherqualify.id} 
     and dcaBTeacherqualify.user_account like concat('%', #{ dcaBTeacherqualify.userAccount} ,'%')
     and dcaBTeacherqualify.tq_code like concat('%', #{ dcaBTeacherqualify.tqCode} ,'%')

    AND dcaBTeacherqualify.tq_receive_date  >=   #{dcaBTeacherqualify.tqReceiveDateFrom}


    AND dcaBTeacherqualify.tq_receive_date  <=   #{dcaBTeacherqualify.tqReceiveDateTo}

 and dcaBTeacherqualify.state =#{ dcaBTeacherqualify.state} 
 and dcaBTeacherqualify.IS_DELETEMARK =#{ dcaBTeacherqualify.isDeletemark} 

    AND dcaBTeacherqualify.CREATE_TIME  >=   #{dcaBTeacherqualify.createTimeFrom}


    AND dcaBTeacherqualify.CREATE_TIME  <=   #{dcaBTeacherqualify.createTimeTo}


    AND dcaBTeacherqualify.MODIFY_TIME  >=   #{dcaBTeacherqualify.modifyTimeFrom}


    AND dcaBTeacherqualify.MODIFY_TIME  <=   #{dcaBTeacherqualify.modifyTimeTo}
