mapper.update:
update dca_b_user
user_account_name=#{userAccountName}, user_account=#{userAccount}, ks=#{ks}, xl=#{xl}, telephone=#{telephone}, dept_name=#{deptName}, position_name=#{positionName}, np_position_name=#{npPositionName}, sex_name=#{sexName}, birthday=#{birthday}, school_date=#{schoolDate}, zyjsgw=#{zyjsgw}, zyjsgw_lc=#{zyjsgwLc}, xcszyjzc=#{xcszyjzc}, appointed_date=#{appointedDate}, patent_ranknum=#{patentRanknum}, is_authority=#{isAuthority}, file_id=#{fileId}, is_zhuanrang=#{isZhuanrang}, file_url=#{fileUrl}, patent_good=#{patentGood}, dca_year=#{dcaYear}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, appointed_date_lc=#{appointedDateLc}, file_id_lc=#{fileIdLc}, file_url_lc=#{fileUrlLc},
where id=#{id}

mapper.select:
select  *  from dca_b_user
where  1=1
     and dcaBUser.id =#{ dcaBUser.id} 
     and dcaBUser.user_account_name like concat('%', #{ dcaBUser.userAccountName} ,'%')
     and dcaBUser.user_account like concat('%', #{ dcaBUser.userAccount} ,'%')
     and dcaBUser.ks like concat('%', #{ dcaBUser.ks} ,'%')
     and dcaBUser.xl like concat('%', #{ dcaBUser.xl} ,'%')
     and dcaBUser.telephone like concat('%', #{ dcaBUser.telephone} ,'%')
     and dcaBUser.dept_name like concat('%', #{ dcaBUser.deptName} ,'%')
     and dcaBUser.position_name like concat('%', #{ dcaBUser.positionName} ,'%')
     and dcaBUser.np_position_name like concat('%', #{ dcaBUser.npPositionName} ,'%')
     and dcaBUser.sex_name like concat('%', #{ dcaBUser.sexName} ,'%')

    AND dcaBUser.birthday  >=   #{dcaBUser.birthdayFrom}


    AND dcaBUser.birthday  <=   #{dcaBUser.birthdayTo}


    AND dcaBUser.school_date  >=   #{dcaBUser.schoolDateFrom}


    AND dcaBUser.school_date  <=   #{dcaBUser.schoolDateTo}

     and dcaBUser.zyjsgw like concat('%', #{ dcaBUser.zyjsgw} ,'%')
     and dcaBUser.zyjsgw_lc like concat('%', #{ dcaBUser.zyjsgwLc} ,'%')
     and dcaBUser.xcszyjzc like concat('%', #{ dcaBUser.xcszyjzc} ,'%')

    AND dcaBUser.appointed_date  >=   #{dcaBUser.appointedDateFrom}


    AND dcaBUser.appointed_date  <=   #{dcaBUser.appointedDateTo}

 and dcaBUser.patent_ranknum =#{ dcaBUser.patentRanknum} 
     and dcaBUser.is_authority like concat('%', #{ dcaBUser.isAuthority} ,'%')
     and dcaBUser.file_id like concat('%', #{ dcaBUser.fileId} ,'%')
     and dcaBUser.is_zhuanrang like concat('%', #{ dcaBUser.isZhuanrang} ,'%')
     and dcaBUser.file_url like concat('%', #{ dcaBUser.fileUrl} ,'%')
     and dcaBUser.patent_good like concat('%', #{ dcaBUser.patentGood} ,'%')
     and dcaBUser.dca_year like concat('%', #{ dcaBUser.dcaYear} ,'%')
 and dcaBUser.state =#{ dcaBUser.state} 
 and dcaBUser.IS_DELETEMARK =#{ dcaBUser.isDeletemark} 

    AND dcaBUser.CREATE_TIME  >=   #{dcaBUser.createTimeFrom}


    AND dcaBUser.CREATE_TIME  <=   #{dcaBUser.createTimeTo}


    AND dcaBUser.MODIFY_TIME  >=   #{dcaBUser.modifyTimeFrom}


    AND dcaBUser.MODIFY_TIME  <=   #{dcaBUser.modifyTimeTo}

     and dcaBUser.auditMan like concat('%', #{ dcaBUser.auditMan} ,'%')
     and dcaBUser.auditManName like concat('%', #{ dcaBUser.auditManName} ,'%')

    AND dcaBUser.auditDate  >=   #{dcaBUser.auditDateFrom}


    AND dcaBUser.auditDate  <=   #{dcaBUser.auditDateTo}

     and dcaBUser.auditSuggestion like concat('%', #{ dcaBUser.auditSuggestion} ,'%')

    AND dcaBUser.appointed_date_lc  >=   #{dcaBUser.appointedDateLcFrom}


    AND dcaBUser.appointed_date_lc  <=   #{dcaBUser.appointedDateLcTo}

     and dcaBUser.file_id_lc like concat('%', #{ dcaBUser.fileIdLc} ,'%')
     and dcaBUser.file_url_lc like concat('%', #{ dcaBUser.fileUrlLc} ,'%')
