mapper.update:
update dca_b_worknum
file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, year=#{year}, mzbrl=#{mzbrl}, glzybrl=#{glzybrl}, remarknote=#{remarknote}, ssbrl=#{ssbrl}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_worknum
where  1=1
     and dcaBWorknum.id =#{ dcaBWorknum.id} 
     and dcaBWorknum.file_id like concat('%', #{ dcaBWorknum.fileId} ,'%')
     and dcaBWorknum.file_url like concat('%', #{ dcaBWorknum.fileUrl} ,'%')
     and dcaBWorknum.user_account_name like concat('%', #{ dcaBWorknum.userAccountName} ,'%')
     and dcaBWorknum.user_account like concat('%', #{ dcaBWorknum.userAccount} ,'%')
 and dcaBWorknum.year =#{ dcaBWorknum.year} 
 and dcaBWorknum.mzbrl =#{ dcaBWorknum.mzbrl} 
 and dcaBWorknum.glzybrl =#{ dcaBWorknum.glzybrl} 
     and dcaBWorknum.remarknote like concat('%', #{ dcaBWorknum.remarknote} ,'%')
 and dcaBWorknum.ssbrl =#{ dcaBWorknum.ssbrl} 
 and dcaBWorknum.state =#{ dcaBWorknum.state} 
 and dcaBWorknum.IS_DELETEMARK =#{ dcaBWorknum.isDeletemark} 

    AND dcaBWorknum.CREATE_TIME  >=   #{dcaBWorknum.createTimeFrom}


    AND dcaBWorknum.CREATE_TIME  <=   #{dcaBWorknum.createTimeTo}


    AND dcaBWorknum.MODIFY_TIME  >=   #{dcaBWorknum.modifyTimeFrom}


    AND dcaBWorknum.MODIFY_TIME  <=   #{dcaBWorknum.modifyTimeTo}

     and dcaBWorknum.auditMan like concat('%', #{ dcaBWorknum.auditMan} ,'%')
     and dcaBWorknum.auditManName like concat('%', #{ dcaBWorknum.auditManName} ,'%')

    AND dcaBWorknum.auditDate  >=   #{dcaBWorknum.auditDateFrom}


    AND dcaBWorknum.auditDate  <=   #{dcaBWorknum.auditDateTo}

     and dcaBWorknum.auditSuggestion like concat('%', #{ dcaBWorknum.auditSuggestion} ,'%')
