mapper.update:
update dca_b_copy_prizeorpunish
dca_year=#{dcaYear}, file_id=#{fileId}, display_index=#{displayIndex}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, pp_start_time=#{ppStartTime}, pp_end_time=#{ppEndTime}, pp_content=#{ppContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_prizeorpunish
where  1=1
     and dcaBCopyPrizeorpunish.id =#{ dcaBCopyPrizeorpunish.id} 
     and dcaBCopyPrizeorpunish.dca_year like concat('%', #{ dcaBCopyPrizeorpunish.dcaYear} ,'%')
     and dcaBCopyPrizeorpunish.file_id like concat('%', #{ dcaBCopyPrizeorpunish.fileId} ,'%')
 and dcaBCopyPrizeorpunish.display_index =#{ dcaBCopyPrizeorpunish.displayIndex} 
     and dcaBCopyPrizeorpunish.file_url like concat('%', #{ dcaBCopyPrizeorpunish.fileUrl} ,'%')
     and dcaBCopyPrizeorpunish.user_account_name like concat('%', #{ dcaBCopyPrizeorpunish.userAccountName} ,'%')
     and dcaBCopyPrizeorpunish.user_account like concat('%', #{ dcaBCopyPrizeorpunish.userAccount} ,'%')

    AND dcaBCopyPrizeorpunish.pp_start_time  >=   #{dcaBCopyPrizeorpunish.ppStartTimeFrom}


    AND dcaBCopyPrizeorpunish.pp_start_time  <=   #{dcaBCopyPrizeorpunish.ppStartTimeTo}


    AND dcaBCopyPrizeorpunish.pp_end_time  >=   #{dcaBCopyPrizeorpunish.ppEndTimeFrom}


    AND dcaBCopyPrizeorpunish.pp_end_time  <=   #{dcaBCopyPrizeorpunish.ppEndTimeTo}

     and dcaBCopyPrizeorpunish.pp_content like concat('%', #{ dcaBCopyPrizeorpunish.ppContent} ,'%')
 and dcaBCopyPrizeorpunish.state =#{ dcaBCopyPrizeorpunish.state} 
 and dcaBCopyPrizeorpunish.IS_DELETEMARK =#{ dcaBCopyPrizeorpunish.isDeletemark} 

    AND dcaBCopyPrizeorpunish.CREATE_TIME  >=   #{dcaBCopyPrizeorpunish.createTimeFrom}


    AND dcaBCopyPrizeorpunish.CREATE_TIME  <=   #{dcaBCopyPrizeorpunish.createTimeTo}


    AND dcaBCopyPrizeorpunish.MODIFY_TIME  >=   #{dcaBCopyPrizeorpunish.modifyTimeFrom}


    AND dcaBCopyPrizeorpunish.MODIFY_TIME  <=   #{dcaBCopyPrizeorpunish.modifyTimeTo}

     and dcaBCopyPrizeorpunish.auditMan like concat('%', #{ dcaBCopyPrizeorpunish.auditMan} ,'%')
     and dcaBCopyPrizeorpunish.auditManName like concat('%', #{ dcaBCopyPrizeorpunish.auditManName} ,'%')

    AND dcaBCopyPrizeorpunish.auditDate  >=   #{dcaBCopyPrizeorpunish.auditDateFrom}


    AND dcaBCopyPrizeorpunish.auditDate  <=   #{dcaBCopyPrizeorpunish.auditDateTo}

     and dcaBCopyPrizeorpunish.auditSuggestion like concat('%', #{ dcaBCopyPrizeorpunish.auditSuggestion} ,'%')
