mapper.update:
update dca_b_copy_user
user_account_name=#{userAccountName}, user_account=#{userAccount}, ks=#{ks}, xl=#{xl}, telephone=#{telephone}, dept_name=#{deptName}, position_name=#{positionName}, np_position_name=#{npPositionName}, sex_name=#{sexName}, birthday=#{birthday}, school_date=#{schoolDate}, zyjsgw=#{zyjsgw}, zyjsgw_lc=#{zyjsgwLc}, xcszyjzc=#{xcszyjzc}, appointed_date=#{appointedDate}, patent_ranknum=#{patentRanknum}, is_authority=#{isAuthority}, file_id=#{fileId}, is_zhuanrang=#{isZhuanrang}, file_url=#{fileUrl}, patent_good=#{patentGood}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, display_index=#{displayIndex}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, appointed_date_lc=#{appointedDateLc}, file_id_lc=#{fileIdLc}, file_url_lc=#{fileUrlLc}, dca_year=#{dcaYear}, yuangongzu=#{yuangongzu}, xrgwjb=#{xrgwjb}, xrgwjbprsj=#{xrgwjbprsj}, djrdzzw=#{djrdzzw},
where id=#{id}

mapper.select:
select  *  from dca_b_copy_user
where  1=1
     and dcaBCopyUser.id =#{ dcaBCopyUser.id} 
     and dcaBCopyUser.user_account_name like concat('%', #{ dcaBCopyUser.userAccountName} ,'%')
     and dcaBCopyUser.user_account like concat('%', #{ dcaBCopyUser.userAccount} ,'%')
     and dcaBCopyUser.ks like concat('%', #{ dcaBCopyUser.ks} ,'%')
     and dcaBCopyUser.xl like concat('%', #{ dcaBCopyUser.xl} ,'%')
     and dcaBCopyUser.telephone like concat('%', #{ dcaBCopyUser.telephone} ,'%')
     and dcaBCopyUser.dept_name like concat('%', #{ dcaBCopyUser.deptName} ,'%')
     and dcaBCopyUser.position_name like concat('%', #{ dcaBCopyUser.positionName} ,'%')
     and dcaBCopyUser.np_position_name like concat('%', #{ dcaBCopyUser.npPositionName} ,'%')
     and dcaBCopyUser.sex_name like concat('%', #{ dcaBCopyUser.sexName} ,'%')

    AND dcaBCopyUser.birthday  >=   #{dcaBCopyUser.birthdayFrom}


    AND dcaBCopyUser.birthday  <=   #{dcaBCopyUser.birthdayTo}


    AND dcaBCopyUser.school_date  >=   #{dcaBCopyUser.schoolDateFrom}


    AND dcaBCopyUser.school_date  <=   #{dcaBCopyUser.schoolDateTo}

     and dcaBCopyUser.zyjsgw like concat('%', #{ dcaBCopyUser.zyjsgw} ,'%')
     and dcaBCopyUser.zyjsgw_lc like concat('%', #{ dcaBCopyUser.zyjsgwLc} ,'%')
     and dcaBCopyUser.xcszyjzc like concat('%', #{ dcaBCopyUser.xcszyjzc} ,'%')

    AND dcaBCopyUser.appointed_date  >=   #{dcaBCopyUser.appointedDateFrom}


    AND dcaBCopyUser.appointed_date  <=   #{dcaBCopyUser.appointedDateTo}

 and dcaBCopyUser.patent_ranknum =#{ dcaBCopyUser.patentRanknum} 
     and dcaBCopyUser.is_authority like concat('%', #{ dcaBCopyUser.isAuthority} ,'%')
     and dcaBCopyUser.file_id like concat('%', #{ dcaBCopyUser.fileId} ,'%')
     and dcaBCopyUser.is_zhuanrang like concat('%', #{ dcaBCopyUser.isZhuanrang} ,'%')
     and dcaBCopyUser.file_url like concat('%', #{ dcaBCopyUser.fileUrl} ,'%')
     and dcaBCopyUser.patent_good like concat('%', #{ dcaBCopyUser.patentGood} ,'%')
 and dcaBCopyUser.state =#{ dcaBCopyUser.state} 
 and dcaBCopyUser.IS_DELETEMARK =#{ dcaBCopyUser.isDeletemark} 

    AND dcaBCopyUser.CREATE_TIME  >=   #{dcaBCopyUser.createTimeFrom}


    AND dcaBCopyUser.CREATE_TIME  <=   #{dcaBCopyUser.createTimeTo}

 and dcaBCopyUser.display_index =#{ dcaBCopyUser.displayIndex} 

    AND dcaBCopyUser.MODIFY_TIME  >=   #{dcaBCopyUser.modifyTimeFrom}


    AND dcaBCopyUser.MODIFY_TIME  <=   #{dcaBCopyUser.modifyTimeTo}

     and dcaBCopyUser.auditMan like concat('%', #{ dcaBCopyUser.auditMan} ,'%')
     and dcaBCopyUser.auditManName like concat('%', #{ dcaBCopyUser.auditManName} ,'%')

    AND dcaBCopyUser.auditDate  >=   #{dcaBCopyUser.auditDateFrom}


    AND dcaBCopyUser.auditDate  <=   #{dcaBCopyUser.auditDateTo}

     and dcaBCopyUser.auditSuggestion like concat('%', #{ dcaBCopyUser.auditSuggestion} ,'%')

    AND dcaBCopyUser.appointed_date_lc  >=   #{dcaBCopyUser.appointedDateLcFrom}


    AND dcaBCopyUser.appointed_date_lc  <=   #{dcaBCopyUser.appointedDateLcTo}

     and dcaBCopyUser.file_id_lc like concat('%', #{ dcaBCopyUser.fileIdLc} ,'%')
     and dcaBCopyUser.file_url_lc like concat('%', #{ dcaBCopyUser.fileUrlLc} ,'%')
     and dcaBCopyUser.dca_year like concat('%', #{ dcaBCopyUser.dcaYear} ,'%')
